ui.UiFactory.customTypes["ui.FreeChoiceBox"] = {
    "type": "ui.FreeLayout",
    "frame": [0, 0, Graphics.width, Graphics.height],
    "controls": [
        {
            "type": "ui.StackLayout",
            "sizeToFit": true,
            "alignmentX": 1,
            "alignmentY": 0,
            "visible": no,
            "formulas": [$ -> o.visible = $tempFields.choiceTimer.behavior.isRunning and $tempFields.choiceTimerVisible],
            "controls": [
                {
                    "type": "ui.Text",
                    "style": "choiceTimerText",
                    "format": "%02d",
                    "sizeToFit": true,
                    "updateBehavior": "continuous",
                    "formulas": [$ -> o.text = $tempFields.choiceTimer.behavior.minutes],
                    "text": ""
                },
                {
                    "type": "ui.Text",
                    "sizeToFit": true,
                    "style": "choiceTimerText",
                    "text": ":"
                },
                {
                    "type": "ui.Text",
                    "style": "choiceTimerText",
                    "sizeToFit": true,
                    "format": "%02d",
                    "updateBehavior": "continuous",
                    "id": "seconds",
                    "formulas": [$ -> o.text = $tempFields.choiceTimer.behavior.seconds],
                    "text": ""
                }
            ]
        },
        {
            "type": "ui.FreeLayout",
            "frame": [0, 0, Graphics.width, Graphics.height],
            "dataField": ($ -> $dataFields.scene.choices),
            "template": {
                "type": "ui.FreeLayout",
                "formulas": [$ -> if @onInitialize then o.ui.enabled = $dataFields.scene.choices[o.index].isEnabled],
                "controls":[
                    {
                        "type": "ui.SelectableWindow",
                        "frame": [0, 0, "100%", "100%"],

                        "params": {
                            "action": {"name": "emitEvent", "params": { "name": "selectionAccept", "source": ($ -> o.parent.parent.parent.parent), "data": ($ -> $dataFields.scene.choices[o.parent.parent.index]) }}
                        },
                        "zIndex": 4999
                    },
                    {
                        "type": "ui.Text",
                        "sizeToFit": true,
                        "styles": ["regularUIText"],
                        "alignmentX": 1,
                        "alignmentY": 1,
                        "frame": [0, 0],
                        "formulas": [$ -> o.text = $dataFields.scene.choices[o.parent.index]?.text],
                        "zIndex": 5100
                    }
                ]
            }

        }

    ]
}

